"use client";
import React from "react";
import "@copilotkit/react-ui/styles.css";
import "./style.css";
import { CopilotKit, useCopilotAction } from "@copilotkit/react-core";
import { CopilotChat } from "@copilotkit/react-ui";

interface AgenticChatProps {
  params: Promise<{
    integrationId: string;
  }>;
}

const AgenticChat: React.FC<AgenticChatProps> = ({ params }) => {
  const { integrationId } = React.use(params);

  return (
    <CopilotKit
      runtimeUrl={`/api/copilotkit/${integrationId}`}
      showDevConsole={false}
      agent="backend_tool_rendering"
    >
      <Chat />
    </CopilotKit>
  );
};

const Chat = () => {
  useCopilotAction({
    name: "get_weather",
    available: "disabled",
    parameters: [{ name: "location", type: "string", required: true }],
    render: ({ args, result, status }) => {
      if (status !== "complete") {
        return (
          <div className=" bg-[#667eea] text-white p-4 rounded-lg max-w-md">
            <span className="animate-spin">‚öôÔ∏è Retrieving weather...</span>
          </div>
        );
      }

      const weatherResult: WeatherToolResult = {
        temperature: result?.temperature || 0,
        conditions: result?.conditions || "clear",
        humidity: result?.humidity || 0,
        windSpeed: result?.wind_speed || 0,
        feelsLike: result?.feels_like || result?.temperature || 0,
      };

      const themeColor = getThemeColor(weatherResult.conditions);

      return (
        <WeatherCard
          location={args.location}
          themeColor={themeColor}
          result={weatherResult}
          status={status || "complete"}
        />
      );
    },
  });

  return (
    <div className="flex justify-center items-center h-full w-full">
      <div className="h-full w-full md:w-8/10 md:h-8/10 rounded-lg">
        <CopilotChat
          className="h-full rounded-2xl max-w-6xl mx-auto"
          labels={{ initial: "Hi! I can look up the weather for you. Just ask!" }}
          suggestions={[
            {
              title: "Weather in San Francisco",
              message: "What's the weather like in San Francisco?",
            },
            {
              title: "Weather in New York",
              message: "Tell me about the weather in New York.",
            },
            {
              title: "Weather in Tokyo",
              message: "How's the weather in Tokyo today?",
            },
          ]}
        />
      </div>
    </div>
  );
};

interface WeatherToolResult {
  temperature: number;
  conditions: string;
  humidity: number;
  windSpeed: number;
  feelsLike: number;
}

function getThemeColor(conditions: string): string {
  const conditionLower = conditions.toLowerCase();
  if (conditionLower.includes("clear") || conditionLower.includes("sunny")) {
    return "#667eea";
  }
  if (conditionLower.includes("rain") || conditionLower.includes("storm")) {
    return "#4A5568";
  }
  if (conditionLower.includes("cloud")) {
    return "#718096";
  }
  if (conditionLower.includes("snow")) {
    return "#63B3ED";
  }
  return "#764ba2";
}

function WeatherCard({
  location,
  themeColor,
  result,
  status,
}: {
  location?: string;
  themeColor: string;
  result: WeatherToolResult;
  status: "inProgress" | "executing" | "complete";
}) {
  return (
    <div
      data-testid="weather-card"
      style={{ backgroundColor: themeColor }}
      className="rounded-xl mt-6 mb-4 max-w-md w-full"
    >
      <div className="bg-white/20 p-4 w-full">
        <div className="flex items-center justify-between">
          <div>
            <h3 data-testid="weather-city" className="text-xl font-bold text-white capitalize">
              {location}
            </h3>
            <p className="text-white">Current Weather</p>
          </div>
          <WeatherIcon conditions={result.conditions} />
        </div>

        <div className="mt-4 flex items-end justify-between">
          <div className="text-3xl font-bold text-white">
            <span className="">{result.temperature}¬∞ C</span>
            <span className="text-sm text-white/50">
              {" / "}
              {((result.temperature * 9) / 5 + 32).toFixed(1)}¬∞ F
            </span>
          </div>
          <div className="text-sm text-white capitalize">{result.conditions}</div>
        </div>

        <div className="mt-4 pt-4 border-t border-white">
          <div className="grid grid-cols-3 gap-2 text-center">
            <div data-testid="weather-humidity">
              <p className="text-white text-xs">Humidity</p>
              <p className="text-white font-medium">{result.humidity}%</p>
            </div>
            <div data-testid="weather-wind">
              <p className="text-white text-xs">Wind</p>
              <p className="text-white font-medium">{result.windSpeed} mph</p>
            </div>
            <div data-testid="weather-feels-like">
              <p className="text-white text-xs">Feels Like</p>
              <p className="text-white font-medium">{result.feelsLike}¬∞</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

function WeatherIcon({ conditions }: { conditions: string }) {
  if (!conditions) return null;

  if (conditions.toLowerCase().includes("clear") || conditions.toLowerCase().includes("sunny")) {
    return <SunIcon />;
  }

  if (
    conditions.toLowerCase().includes("rain") ||
    conditions.toLowerCase().includes("drizzle") ||
    conditions.toLowerCase().includes("snow") ||
    conditions.toLowerCase().includes("thunderstorm")
  ) {
    return <RainIcon />;
  }

  if (
    conditions.toLowerCase().includes("fog") ||
    conditions.toLowerCase().includes("cloud") ||
    conditions.toLowerCase().includes("overcast")
  ) {
    return <CloudIcon />;
  }

  return <CloudIcon />;
}

// Simple sun icon for the weather card
function SunIcon() {
  return (
    <svg
      xmlns="http://www.w3.org/2000/svg"
      viewBox="0 0 24 24"
      fill="currentColor"
      className="w-14 h-14 text-yellow-200"
    >
      <circle cx="12" cy="12" r="5" />
      <path
        d="M12 1v2M12 21v2M4.22 4.22l1.42 1.42M18.36 18.36l1.42 1.42M1 12h2M21 12h2M4.22 19.78l1.42-1.42M18.36 5.64l1.42-1.42"
        strokeWidth="2"
        stroke="currentColor"
      />
    </svg>
  );
}

function RainIcon() {
  return (
    <svg
      xmlns="http://www.w3.org/2000/svg"
      viewBox="0 0 24 24"
      fill="currentColor"
      className="w-14 h-14 text-blue-200"
    >
      {/* Cloud */}
      <path
        d="M7 15a4 4 0 0 1 0-8 5 5 0 0 1 10 0 4 4 0 0 1 0 8H7z"
        fill="currentColor"
        opacity="0.8"
      />
      {/* Rain drops */}
      <path
        d="M8 18l2 4M12 18l2 4M16 18l2 4"
        stroke="currentColor"
        strokeWidth="2"
        strokeLinecap="round"
        fill="none"
      />
    </svg>
  );
}

function CloudIcon() {
  return (
    <svg
      xmlns="http://www.w3.org/2000/svg"
      viewBox="0 0 24 24"
      fill="currentColor"
      className="w-14 h-14 text-gray-200"
    >
      <path d="M7 15a4 4 0 0 1 0-8 5 5 0 0 1 10 0 4 4 0 0 1 0 8H7z" fill="currentColor" />
    </svg>
  );
}

export default AgenticChat;

.copilotKitInput {
  border-bottom-left-radius: 0.75rem;
  border-bottom-right-radius: 0.75rem;
  border-top-left-radius: 0.75rem;
  border-top-right-radius: 0.75rem;
  border: 1px solid var(--copilot-kit-separator-color) !important;
}

.copilotKitChat {
  background-color: #fff !important;
}

server-starter-all-features::human_in_th.content:
"use client";
import React, { useState, useEffect } from "react";
import "@copilotkit/react-ui/styles.css";
import "./style.css";
import {
  CopilotKit,
  useHumanInTheLoop,
  useLangGraphInterrupt,
} from "@copilotkit/react-core";
import { CopilotChat } from "@copilotkit/react-ui";
import { useTheme } from "next-themes";

interface HumanInTheLoopProps {
  params: Promise<{
    integrationId: string;
  }>;
}

const HumanInTheLoop: React.FC<HumanInTheLoopProps> = ({ params }) => {
  const { integrationId } = React.use(params);

  return (
    <CopilotKit
      runtimeUrl={`/api/copilotkit/${integrationId}`}
      showDevConsole={false}
      // agent lock to the relevant agent
      agent="human_in_the_loop"
    >
      <Chat integrationId={integrationId} />
    </CopilotKit>
  );
};

interface Step {
  description: string;
  status: "disabled" | "enabled" | "executing";
}

// Shared UI Components
const StepContainer = ({ theme, children }: { theme?: string; children: React.ReactNode }) => (
  <div data-testid="select-steps" className="flex">
    <div
      className={`relative rounded-xl w-[600px] p-6 shadow-lg backdrop-blur-sm ${
        theme === "dark"
          ? "bg-gradient-to-br from-slate-900 via-slate-800 to-slate-900 text-white border border-slate-700/50 shadow-2xl"
          : "bg-gradient-to-br from-white via-gray-50 to-white text-gray-800 border border-gray-200/80"
      }`}
    >
      {children}
    </div>
  </div>
);

const StepHeader = ({
  theme,
  enabledCount,
  totalCount,
  status,
  showStatus = false,
}: {
  theme?: string;
  enabledCount: number;
  totalCount: number;
  status?: string;
  showStatus?: boolean;
}) => (
  <div className="mb-5">
    <div className="flex items-center justify-between mb-3">
      <h2 className="text-xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
        Select Steps
      </h2>
      <div className="flex items-center gap-3">
        <div className={`text-sm ${theme === "dark" ? "text-slate-400" : "text-gray-500"}`}>
          {enabledCount}/{totalCount} Selected
        </div>
        {showStatus && (
          <div
            className={`text-xs px-2 py-1 rounded-full font-medium ${
              status === "executing"
                ? theme === "dark"
                  ? "bg-blue-900/30 text-blue-300 border border-blue-500/30"
                  : "bg-blue-50 text-blue-600 border border-blue-200"
                : theme === "dark"
                  ? "bg-slate-700 text-slate-300"
                  : "bg-gray-100 text-gray-600"
            }`}
          >
            {status === "executing" ? "Ready" : "Waiting"}
          </div>
        )}
      </div>
    </div>

    <div
      className={`relative h-2 rounded-full overflow-hidden ${theme === "dark" ? "bg-slate-700" : "bg-gray-200"}`}
    >
      <div
        className="absolute top-0 left-0 h-full bg-gradient-to-r from-blue-500 to-purple-500 rounded-full transition-all duration-500 ease-out"
        style={{ width: `${totalCount > 0 ? (enabledCount / totalCount) * 100 : 0}%` }}
      />
    </div>
  </div>
);

const StepItem = ({
  step,
  theme,
  status,
  onToggle,
  disabled = false,
}: {
  step: { description: string; status: string };
  theme?: string;
  status?: string;
  onToggle: () => void;
  disabled?: boolean;
}) => (
  <div
    className={`flex items-center p-3 rounded-lg transition-all duration-300 ${
      step.status === "enabled"
        ? theme === "dark"
          ? "bg-gradient-to-r from-blue-900/20 to-purple-900/10 border border-blue-500/30"
          : "bg-gradient-to-r from-blue-50 to-purple-50 border border-blue-200/60"
        : theme === "dark"
          ? "bg-slate-800/30 border border-slate-600/30"
          : "bg-gray-50/50 border border-gray-200/40"
    }`}
  >
    <label data-testid="step-item" className="flex items-center cursor-pointer w-full">
      <div className="relative">
        <input
          type="checkbox"
          checked={step.status === "enabled"}
          onChange={onToggle}
          className="sr-only"
          disabled={disabled}
        />
        <div
          className={`w-5 h-5 rounded border-2 flex items-center justify-center transition-all duration-200 ${
            step.status === "enabled"
              ? "bg-gradient-to-br from-blue-500 to-purple-600 border-blue-500"
              : theme === "dark"
                ? "border-slate-400 bg-slate-700"
                : "border-gray-300 bg-white"
          } ${disabled ? "opacity-60" : ""}`}
        >
          {step.status === "enabled" && (
            <svg
              className="w-3 h-3 text-white"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={3}
                d="M5 13l4 4L19 7"
              />
            </svg>
          )}
        </div>
      </div>
      <span
        data-testid="step-text"
        className={`ml-3 font-medium transition-all duration-300 ${
          step.status !== "enabled" && status != "inProgress"
            ? `line-through ${theme === "dark" ? "text-slate-500" : "text-gray-400"}`
            : theme === "dark"
              ? "text-white"
              : "text-gray-800"
        } ${disabled ? "opacity-60" : ""}`}
      >
        {step.description}
      </span>
    </label>
  </div>
);

const ActionButton = ({
  variant,
  theme,
  disabled,
  onClick,
  children,
}: {
  variant: "primary" | "secondary" | "success" | "danger";
  theme?: string;
  disabled?: boolean;
  onClick: () => void;
  children: React.ReactNode;
}) => {
  const baseClasses = "px-6 py-3 rounded-lg font-semibold transition-all duration-200";
  const enabledClasses = "hover:scale-105 shadow-md hover:shadow-lg";
  const disabledClasses = "opacity-50 cursor-not-allowed";

  const variantClasses = {
    primary:
      "bg-gradient-to-r from-purple-500 to-purple-700 hover:from-purple-600 hover:to-purple-800 text-white shadow-lg hover:shadow-xl",
    secondary:
      theme === "dark"
        ? "bg-slate-700 hover:bg-slate-600 text-white border border-slate-600 hover:border-slate-500"
        : "bg-gray-100 hover:bg-gray-200 text-gray-800 border border-gray-300 hover:border-gray-400",
    success:
      "bg-gradient-to-r from-green-500 to-emerald-600 hover:from-green-600 hover:to-emerald-700 text-white shadow-lg hover:shadow-xl",
    danger:
      "bg-gradient-to-r from-red-500 to-red-600 hover:from-red-600 hover:to-red-700 text-white shadow-lg hover:shadow-xl",
  };

  return (
    <button
      className={`${baseClasses} ${disabled ? disabledClasses : enabledClasses} ${
        disabled && variant === "secondary"
          ? "bg-gray-200 text-gray-500"
          : disabled && variant === "success"
            ? "bg-gray-400"
            : variantClasses[variant]
      }`}
      disabled={disabled}
      onClick={onClick}
    >
      {children}
    </button>
  );
};

const DecorativeElements = ({
  theme,
  variant = "default",
}: {
  theme?: string;
  variant?: "default" | "success" | "danger";
}) => (
  <>
    <div
      className={`absolute top-3 right-3 w-16 h-16 rounded-full blur-xl ${
        variant === "success"
          ? theme === "dark"
            ? "bg-gradient-to-br from-green-500/10 to-emerald-500/10"
            : "bg-gradient-to-br from-green-200/30 to-emerald-200/30"
          : variant === "danger"
            ? theme === "dark"
              ? "bg-gradient-to-br from-red-500/10 to-pink-500/10"
              : "bg-gradient-to-br from-red-200/30 to-pink-200/30"
            : theme === "dark"
              ? "bg-gradient-to-br from-blue-500/10 to-purple-500/10"
              : "bg-gradient-to-br from-blue-200/30 to-purple-200/30"
      }`}
    />
    <div
      className={`absolute bottom-3 left-3 w-12 h-12 rounded-full blur-xl ${
        variant === "default"
          ? theme === "dark"
            ? "bg-gradient-to-br from-purple-500/10 to-pink-500/10"
            : "bg-gradient-to-br from-purple-200/30 to-pink-200/30"
          : "opacity-50"
      }`}
    />
  </>
);
const InterruptHumanInTheLoop: React.FC<{
  event: { value: { steps: Step[] } };
  resolve: (value: string) => void;
}> = ({ event, resolve }) => {
  const { theme } = useTheme();

  // Parse and initialize steps data
  let initialSteps: Step[] = [];
  if (event.value && event.value.steps && Array.isArray(event.value.steps)) {
    initialSteps = event.value.steps.map((step: any) => ({
      description: typeof step === "string" ? step : step.description || "",
      status: typeof step === "object" && step.status ? step.status : "enabled",
    }));
  }

  const [localSteps, setLocalSteps] = useState<Step[]>(initialSteps);
  const enabledCount = localSteps.filter((step) => step.status === "enabled").length;

  const handleStepToggle = (index: number) => {
    setLocalSteps((prevSteps) =>
      prevSteps.map((step, i) =>
        i === index
          ? { ...step, status: step.status === "enabled" ? "disabled" : "enabled" }
          : step,
      ),
    );
  };

  const handlePerformSteps = () => {
    const selectedSteps = localSteps
      .filter((step) => step.status === "enabled")
      .map((step) => step.description);
    resolve("The user selected the following steps: " + selectedSteps.join(", "));
  };

  return (
    <StepContainer theme={theme}>
      <StepHeader theme={theme} enabledCount={enabledCount} totalCount={localSteps.length} />

      <div className="space-y-3 mb-6">
        {localSteps.map((step, index) => (
          <StepItem
            key={index}
            step={step}
            theme={theme}
            onToggle={() => handleStepToggle(index)}
          />
        ))}
      </div>

      <div className="flex justify-center">
        <ActionButton variant="primary" theme={theme} onClick={handlePerformSteps}>
          <span className="text-lg">‚ú®</span>
          Perform Steps
          <span
            className={`ml-1 px-2 py-1 rounded-full text-xs font-bold ${
              theme === "dark" ? "bg-purple-800/50" : "bg-purple-600/20"
            }`}
          >
            {enabledCount}
          </span>
        </ActionButton>
      </div>

      <DecorativeElements theme={theme} />
    </StepContainer>
  );
};

const Chat = ({ integrationId }: { integrationId: string }) => {
  // Langgraph uses it's own hook to handle human-in-the-loop interactions via langgraph interrupts,
  // This hook won't do anything for other integrations.
  useLangGraphInterrupt({
    render: ({ event, resolve }) => <InterruptHumanInTheLoop event={event} resolve={resolve} />,
  });
  useHumanInTheLoop({
    name: "generate_task_steps",
    description: "Generates a list of steps for the user to perform",
    parameters: [
      {
        name: "steps",
        type: "object[]",
        attributes: [
          {
            name: "description",
            type: "string",
          },
          {
            name: "status",
            type: "string",
            enum: ["enabled", "disabled", "executing"],
          },
        ],
      },
    ],
    // Langgraph uses it's own hook to handle human-in-the-loop interactions via langgraph interrupts,
    // so don't use this action for langgraph integration.
    available: ["langgraph", "langgraph-fastapi", "langgraph-typescript"].includes(integrationId)
      ? "disabled"
      : "enabled",
    render: ({ args, respond, status }) => {
      return <StepsFeedback args={args} respond={respond} status={status} />;
    },
  });

  return (
    <div className="flex justify-center items-center h-full w-full">
      <div className="h-full w-full md:w-8/10 md:h-8/10 rounded-lg">
        <CopilotChat
          suggestions={[
            { title: "Simple plan", message: "Please plan a trip to mars in 5 steps." },
            { title: "Complex plan", message: "Please plan a pastra dish in 10 steps." },
          ]}
          className="h-full rounded-2xl max-w-6xl mx-auto"
          labels={{
            initial:
              "Hi, I'm an agent specialized in helping you with your tasks. How can I help you?",
          }}
        />
      </div>
    </div>
  );
};

const StepsFeedback = ({ args, respond, status }: { args: any; respond: any; status: any }) => {
  const { theme } = useTheme();
  const [localSteps, setLocalSteps] = useState<Step[]>([]);
  const [accepted, setAccepted] = useState<boolean | null>(null);

  useEffect(() => {
    if (status === "executing" && localSteps.length === 0) {
      setLocalSteps(args.steps);
    }
  }, [status, args.steps, localSteps]);

  if (args.steps === undefined || args.steps.length === 0) {
    return <></>;
  }

  const steps = localSteps.length > 0 ? localSteps : args.steps;
  const enabledCount = steps.filter((step: any) => step.status === "enabled").length;

  const handleStepToggle = (index: number) => {
    setLocalSteps((prevSteps) =>
      prevSteps.map((step, i) =>
        i === index
          ? { ...step, status: step.status === "enabled" ? "disabled" : "enabled" }
          : step,
      ),
    );
  };

  const handleReject = () => {
    if (respond) {
      setAccepted(false);
      respond({ accepted: false });
    }
  };

  const handleConfirm = () => {
    if (respond) {
      setAccepted(true);
      respond({ accepted: true, steps: localSteps.filter((step) => step.status === "enabled") });
    }
  };

  return (
    <StepContainer theme={theme}>
      <StepHeader
        theme={theme}
        enabledCount={enabledCount}
        totalCount={steps.length}
        status={status}
        showStatus={true}
      />

      <div className="space-y-3 mb-6">
        {steps.map((step: any, index: any) => (
          <StepItem
            key={index}
            step={step}
            theme={theme}
            status={status}
            onToggle={() => handleStepToggle(index)}
            disabled={status !== "executing"}
          />
        ))}
      </div>

      {/* Action Buttons - Different logic from InterruptHumanInTheLoop */}
      {accepted === null && (
        <div className="flex justify-center gap-4">
          <ActionButton
            variant="secondary"
            theme={theme}
            disabled={status !== "executing"}
            onClick={handleReject}
          >
            <span className="mr-2">‚úó</span>
            Reject
          </ActionButton>
          <ActionButton
            variant="success"
            theme={theme}
            disabled={status !== "executing"}
            onClick={handleConfirm}
          >
            <span className="mr-2">‚úì</span>
            Confirm
            <span
              className={`ml-2 px-2 py-1 rounded-full text-xs font-bold ${
                theme === "dark" ? "bg-green-800/50" : "bg-green-600/20"
              }`}
            >
              {enabledCount}
            </span>
          </ActionButton>
        </div>
      )}

      {/* Result State - Unique to StepsFeedback */}
      {accepted !== null && (
        <div className="flex justify-center">
          <div
            className={`px-6 py-3 rounded-lg font-semibold flex items-center gap-2 ${
              accepted
                ? theme === "dark"
                  ? "bg-green-900/30 text-green-300 border border-green-500/30"
                  : "bg-green-50 text-green-700 border border-green-200"
                : theme === "dark"
                  ? "bg-red-900/30 text-red-300 border border-red-500/30"
                  : "bg-red-50 text-red-700 border border-red-200"
            }`}
          >
            <span className="text-lg">{accepted ? "‚úì" : "‚úó"}</span>
            {accepted ? "Accepted" : "Rejected"}
          </div>
        </div>
      )}

      <DecorativeElements
        theme={theme}
        variant={accepted === true ? "success" : accepted === false ? "danger" : "default"}
      />
    </StepContainer>
  );
};

export default HumanInTheLoop;

.copilotKitInput {
  border-bottom-left-radius: 0.75rem;
  border-bottom-right-radius: 0.75rem;
  border-top-left-radius: 0.75rem;
  border-top-right-radius: 0.75rem;
  border: 1px solid var(--copilot-kit-separator-color) !important;
}

.copilotKitChat {
  background-color: #fff !important;
}

server-starter-all-features::agentic_gen.content:
"use client";
import React from "react";
import "@copilotkit/react-ui/styles.css";
import "./style.css";
import { CopilotKit, useCoAgentStateRender } from "@copilotkit/react-core";
import { CopilotChat } from "@copilotkit/react-ui";
import { useTheme } from "next-themes";

interface AgenticGenerativeUIProps {
  params: Promise<{
    integrationId: string;
  }>;
}

const AgenticGenerativeUI: React.FC<AgenticGenerativeUIProps> = ({ params }) => {
  const { integrationId } = React.use(params);
  return (
    <CopilotKit
      runtimeUrl={`/api/copilotkit/${integrationId}`}
      showDevConsole={false}
      // agent lock to the relevant agent
      agent="agentic_generative_ui"
    >
      <Chat />
    </CopilotKit>
  );
};

interface AgentState {
  steps: {
    description: string;
    status: "pending" | "completed";
  }[];
}

const Chat = () => {
  const { theme } = useTheme();
  useCoAgentStateRender<AgentState>({
    name: "agentic_generative_ui",
    render: ({ state }) => {
      if (!state.steps || state.steps.length === 0) {
        return null;
      }

      const completedCount = state.steps.filter((step) => step.status === "completed").length;
      const progressPercentage = (completedCount / state.steps.length) * 100;

      return (
        <div className="flex">
          <div
            data-testid="task-progress"
            className={`relative rounded-xl w-[700px] p-6 shadow-lg backdrop-blur-sm ${
              theme === "dark"
                ? "bg-gradient-to-br from-slate-900 via-slate-800 to-slate-900 text-white border border-slate-700/50 shadow-2xl"
                : "bg-gradient-to-br from-white via-gray-50 to-white text-gray-800 border border-gray-200/80"
            }`}
          >
            {/* Header */}
            <div className="mb-5">
              <div className="flex items-center justify-between mb-3">
                <h3 className="text-xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
                  Task Progress
                </h3>
                <div className={`text-sm ${theme === "dark" ? "text-slate-400" : "text-gray-500"}`}>
                  {completedCount}/{state.steps.length} Complete
                </div>
              </div>

              {/* Progress Bar */}
              <div
                className={`relative h-2 rounded-full overflow-hidden ${theme === "dark" ? "bg-slate-700" : "bg-gray-200"}`}
              >
                <div
                  className="absolute top-0 left-0 h-full bg-gradient-to-r from-blue-500 to-purple-500 rounded-full transition-all duration-1000 ease-out"
                  style={{ width: `${progressPercentage}%` }}
                />
                <div
                  className={`absolute top-0 left-0 h-full w-full bg-gradient-to-r from-transparent to-transparent animate-pulse ${
                    theme === "dark" ? "via-white/20" : "via-white/40"
                  }`}
                />
              </div>
            </div>

            {/* Steps */}
            <div className="space-y-2">
              {state.steps.map((step, index) => {
                const isCompleted = step.status === "completed";
                const isCurrentPending =
                  step.status === "pending" &&
                  index === state.steps.findIndex((s) => s.status === "pending");
                const isFuturePending = step.status === "pending" && !isCurrentPending;

                return (
                  <div
                    key={index}
                    className={`relative flex items-center p-2.5 rounded-lg transition-all duration-500 ${
                      isCompleted
                        ? theme === "dark"
                          ? "bg-gradient-to-r from-green-900/30 to-emerald-900/20 border border-green-500/30"
                          : "bg-gradient-to-r from-green-50 to-emerald-50 border border-green-200/60"
                        : isCurrentPending
                          ? theme === "dark"
                            ? "bg-gradient-to-r from-blue-900/40 to-purple-900/30 border border-blue-500/50 shadow-lg shadow-blue-500/20"
                            : "bg-gradient-to-r from-blue-50 to-purple-50 border border-blue-200/60 shadow-md shadow-blue-200/50"
                          : theme === "dark"
                            ? "bg-slate-800/50 border border-slate-600/30"
                            : "bg-gray-50/50 border border-gray-200/60"
                    }`}
                  >
                    {/* Connector Line */}
                    {index < state.steps.length - 1 && (
                      <div
                        className={`absolute left-5 top-full w-0.5 h-2 bg-gradient-to-b ${
                          theme === "dark"
                            ? "from-slate-500 to-slate-600"
                            : "from-gray-300 to-gray-400"
                        }`}
                      />
                    )}

                    {/* Status Icon */}
                    <div
                      className={`flex-shrink-0 w-6 h-6 rounded-full flex items-center justify-center mr-2 ${
                        isCompleted
                          ? theme === "dark"
                            ? "bg-gradient-to-br from-green-500 to-emerald-600 shadow-lg shadow-green-500/30"
                            : "bg-gradient-to-br from-green-500 to-emerald-600 shadow-md shadow-green-200"
                          : isCurrentPending
                            ? theme === "dark"
                              ? "bg-gradient-to-br from-blue-500 to-purple-600 shadow-lg shadow-blue-500/30"
                              : "bg-gradient-to-br from-blue-500 to-purple-600 shadow-md shadow-blue-200"
                            : theme === "dark"
                              ? "bg-slate-700 border border-slate-600"
                              : "bg-gray-300 border border-gray-400"
                      }`}
                    >
                      {isCompleted ? (
                        <CheckIcon />
                      ) : isCurrentPending ? (
                        <SpinnerIcon />
                      ) : (
                        <ClockIcon theme={theme} />
                      )}
                    </div>

                    {/* Step Content */}
                    <div className="flex-1 min-w-0">
                      <div
                        data-testid="task-step-text"
                        className={`font-semibold transition-all duration-300 text-sm ${
                          isCompleted
                            ? theme === "dark"
                              ? "text-green-300"
                              : "text-green-700"
                            : isCurrentPending
                              ? theme === "dark"
                                ? "text-blue-300 text-base"
                                : "text-blue-700 text-base"
                              : theme === "dark"
                                ? "text-slate-400"
                                : "text-gray-500"
                        }`}
                      >
                        {step.description}
                      </div>
                      {isCurrentPending && (
                        <div
                          className={`text-sm mt-1 animate-pulse ${
                            theme === "dark" ? "text-blue-400" : "text-blue-600"
                          }`}
                        >
                          Processing...
                        </div>
                      )}
                    </div>

                    {/* Animated Background for Current Step */}
                    {isCurrentPending && (
                      <div
                        className={`absolute inset-0 rounded-lg bg-gradient-to-r animate-pulse ${
                          theme === "dark"
                            ? "from-blue-500/10 to-purple-500/10"
                            : "from-blue-100/50 to-purple-100/50"
                        }`}
                      />
                    )}
                  </div>
                );
              })}
            </div>

            {/* Decorative Elements */}
            <div
              className={`absolute top-3 right-3 w-16 h-16 rounded-full blur-xl ${
                theme === "dark"
                  ? "bg-gradient-to-br from-blue-500/10 to-purple-500/10"
                  : "bg-gradient-to-br from-blue-200/30 to-purple-200/30"
              }`}
            />
            <div
              className={`absolute bottom-3 left-3 w-12 h-12 rounded-full blur-xl ${
                theme === "dark"
                  ? "bg-gradient-to-br from-green-500/10 to-emerald-500/10"
                  : "bg-gradient-to-br from-green-200/30 to-emerald-200/30"
              }`}
            />
          </div>
        </div>
      );
    },
  });

  return (
    <div className="flex justify-center items-center h-full w-full">
      <div className="h-full w-full md:w-8/10 md:h-8/10 rounded-lg">
        <CopilotChat
          className="h-full rounded-2xl max-w-6xl mx-auto"
          labels={{
            initial:
              "Hi, I'm an agent! I can help you with anything you need and will show you progress as I work. What can I do for you?",
          }}
          suggestions={[
            {
              title: "Simple plan",
              message: "Please build a plan to go to mars in 5 steps.",
            },
            {
              title: "Complex plan",
              message: "Please build a plan to go to make pizza in 10 steps.",
            },
          ]}
        />
      </div>
    </div>
  );
};

// Enhanced Icons
function CheckIcon() {
  return (
    <svg className="w-4 h-4 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={3} d="M5 13l4 4L19 7" />
    </svg>
  );
}

function SpinnerIcon() {
  return (
    <svg
      className="w-4 h-4 animate-spin text-white"
      xmlns="http://www.w3.org/2000/svg"
      fill="none"
      viewBox="0 0 24 24"
    >
      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" />
      <path
        className="opacity-75"
        fill="currentColor"
        d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
      />
    </svg>
  );
}

function ClockIcon({ theme }: { theme?: string }) {
  return (
    <svg
      className={`w-3 h-3 ${theme === "dark" ? "text-slate-400" : "text-gray-600"}`}
      fill="none"
      stroke="currentColor"
      viewBox="0 0 24 24"
    >
      <circle cx="12" cy="12" r="10" strokeWidth="2" />
      <polyline points="12,6 12,12 16,14" strokeWidth="2" />
    </svg>
