  "Itsukushima_Shrine_Miyajima_Floating_Torii_Gate_Sunset_Long_Exposure.jpg",
  "Takachiho_Gorge_Waterfall_River_Lush_Greenery_Japan.jpg",
  "Bonsai_Tree_Potted_Japanese_Art_Green_Foliage.jpeg",
  "Shirakawa-go_Gassho-zukuri_Thatched_Roof_Village_Aerial_View.jpg",
  "Ginkaku-ji_Silver_Pavilion_Kyoto_Japanese_Garden_Pond_Reflection.jpg",
  "Senso-ji_Temple_Asakusa_Cherry_Blossoms_Kimono_Umbrella.jpg",
  "Cherry_Blossoms_Sakura_Night_View_City_Lights_Japan.jpg",
  "Mount_Fuji_Lake_Reflection_Cherry_Blossoms_Sakura_Spring.jpg",
];

function HaikuDisplay() {
  const [activeIndex, setActiveIndex] = useState(0);
  const [haikus, setHaikus] = useState<Haiku[]>([
    {
      japanese: ["‰ªÆ„ÅÆÂè•„Çà", "„Åæ„Å£„Åï„Çâ„Å™„Åå„Çâ", "Ëä±„ÇíÂëº„Å∂"],
      english: ["A placeholder verse‚Äî", "even in a blank canvas,", "it beckons flowers."],
      image_name: null,
      gradient: "",
    },
  ]);

  useCopilotAction(
    {
      name: "generate_haiku",
      parameters: [
        {
          name: "japanese",
          type: "string[]",
          required: true,
          description: "3 lines of haiku in Japanese",
        },
        {
          name: "english",
          type: "string[]",
          required: true,
          description: "3 lines of haiku translated to English",
        },
        {
          name: "image_name",
          type: "string",
          required: true,
          description: `One relevant image name from: ${VALID_IMAGE_NAMES.join(", ")}`,
        },
        {
          name: "gradient",
          type: "string",
          required: true,
          description: "CSS Gradient color for the background",
        },
      ],
      followUp: false,
      handler: async ({ japanese, english, image_name, gradient }) => {
        const newHaiku: Haiku = {
          japanese: japanese || [],
          english: english || [],
          image_name: image_name || null,
          gradient: gradient || "",
        };
        setHaikus((prev) => [
          newHaiku,
