        ]);
        setActiveIndex(0);
        return "Haiku generated!";
      },
      render: ({ args }) => {
        if (!args.japanese) return <></>;
        return <HaikuCard haiku={args as Haiku} />;
      },
    },
    [haikus],
  );

  const currentHaiku = haikus[activeIndex];

  return (
